mapper.update:
UPDATE contents
SET views = views + 1
WHERE id = #{id}

UPDATE contents
SET deleted = 1
WHERE id = #{id}

mapper.select:
SELECT * FROM contents WHERE board_type = #{boardType}
                         AND  deleted=0
ORDER BY id DESC LIMIT #{pageIndex}, #{pageSize}

mapper.update:
UPDATE replies
SET deleted = 1
WHERE id = #{replyId}

mapper.select:
select * from contents
where board_type=1
  and locale = #{locale}
  and subcategory =#{subcategory}
  and deleted=0
order by id desc;

select * from contents
where board_type=1
  and locale = #{locale}
  and subcategory =#{subcategory}
  and deleted=0
order by id desc;

select count(*) from contents
where board_type=1
  and locale = #{locale}
  and subcategory =#{subcategory}
  and deleted=0

SELECT * FROM contents
where board_type=1


        and locale = #{locale}


        and subcategory =#{subcategory}

  and deleted=0
ORDER BY id DESC LIMIT #{pageIndex}, #{pageSize}

SELECT COUNT(*) as like_count
FROM contents c
JOIN likes l ON c.id = l.contents_id
WHERE c.deleted = 0
  AND l.user_id =  #{userId}
  AND c.id = #{contentsid}

SELECT COUNT(*) FROM replies
where contents_id=#{id}
and deleted=0
